@import url("https://fonts.googleapis.com/css2?family=Charm:wght@400;700&family=Montserrat:wght@100;400;800&display=swap");
.nav {
  width: 100vw;
  height: 10vh;
  display: grid;
  place-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 2;
}

.nav-wrapper {
  width: 90%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.nav-wrapper__logo {
  display: flex;
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 14%;
}
.nav-wrapper__logo__txt {
  font-size: 1.5rem;
  color: var(--black);
}
.nav-wrapper__btn {
  height: 4rem;
  width: 4rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
}

.nav-wrapper__btn span {
  width: 100%;
  height: 0.2rem;
  background-color: var(--black);
  display: inline-block;
  transition: all 0.4s ease;
}
.nav-wrapper__btn span.btn2 {
  transform: translateX(-10px);
}

.nav-wrapper__btn:hover .btn1,
.nav-wrapper__btn:hover .btn3 {
  transform: translateX(-10px);
}
.nav-wrapper__btn:hover .btn2 {
  transform: translateX(0);
}

:root {
  --white: #fff;
  --smoke: #f8f8f8;
  --grey: #101010;
  --google: #d34836;
  --linkedin: #007bb5;
  --github: #32506d;
  --blue: #002266;
  --l-grey: #939393;
  --gold: #665600;
  --font-main: "Montserrat", sans-serif;
  --font-sec: "Charm", cursive;
  --black: #000;
}

* {
  margin: 0;
  padding: 0;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
}
@media only screen and (max-width: 1007px) {
  html {
    font-size: 57%;
  }
}
@media only screen and (max-width: 830px) {
  html {
    font-size: 50%;
  }
}

body {
  font-family: var(--font-main);
  font-weight: 400;
  line-height: 1.6;
  overflow-y: scroll;
  scrollbar-width: none;
  /* Firefox */
  -ms-overflow-style: none;
}

body::-webkit-scrollbar {
  /* WebKit */
  width: 0;
  height: 0;
}

.intro-loader {
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: var(--black);
  display: grid;
  place-items: center;
  z-index: 5;
}
.intro-loader__txt {
  color: var(--white);
  font-size: 4.8rem;
  position: absolute;
  top: 70%;
  left: 40%;
  transform: translate(-50%, -50%);
  letter-spacing: 0.1rem;
  opacity: 0;
  z-index: 7;
  font-weight: 400;
}
.intro-loader__txt span {
  font-family: var(--font-sec);
  font-size: 9.8rem;
  letter-spacing: 0.2rem;
  font-weight: 800;
  position: relative;
  transform: translate(20%, 20%);
  display: inline-block;
}
.intro-loader__wrapper {
  width: 30%;
  height: 75%;
  position: relative;
  z-index: 3;
  overflow: hidden;
}
.intro-loader__wrapper__mask {
  width: 100%;
  height: 100%;
  background: #000000;
  /* fallback for old browsers */
  background: -webkit-linear-gradient(to right, #434343, #000000);
  /* Chrome 10-25, Safari 5.1-6 */
  background: linear-gradient(to right, #434343, #000000);
  /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
  transform: translateY(101%);
  position: absolute;
}
.intro-loader__wrapper__img {
  width: 100%;
  height: 100%;
  background-image: url("/f81adb3003b90d7281b15a72fecc3164.jpg");
  background-size: cover;
  position: relative;
  top: 0;
  left: 0;
  z-index: 5;
  filter: opacity(0.4);
  transform: translateY(101%);
}

.svg-wrapper {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.logo path:nth-child(1) {
  stroke-dasharray: 475.2982;
  stroke-dashoffset: 475.2982;
}

.logo path:nth-child(2) {
  stroke-dasharray: 381.72;
  stroke-dashoffset: 381.72;
}

.logo path:nth-child(3) {
  stroke-dasharray: 60.784;
  stroke-dashoffset: 60.784;
}

.logo path:nth-child(4) {
  stroke-dasharray: 60.783;
  stroke-dashoffset: 60.783;
}

.logo path:nth-child(5) {
  stroke-dasharray: 31.1271;
  stroke-dashoffset: 31.1271;
}

.logo path:nth-child(6) {
  stroke-dasharray: 0.4;
  stroke-dashoffset: 0.4;
}

.logo path:nth-child(7) {
  stroke-dasharray: 61.25;
  stroke-dashoffset: 61.25;
}

.logo path:nth-child(8) {
  stroke-dasharray: 60.797;
  stroke-dashoffset: 60.797;
}

.logo path:nth-child(9) {
  stroke-dasharray: 42.426071;
  stroke-dashoffset: 42.426071;
}

.logo path:nth-child(10) {
  stroke-dasharray: 862.083;
  stroke-dashoffset: 862.083;
}

.main {
  width: 100vw;
  height: 100vh;
  display: grid;
  grid-template-columns: 5% 5% 15% 50% 15% 5% 5%;
  grid-template-rows: 50vh 40vh 10vh;
  place-items: center;
}
.main .flag {
  width: 50%;
  height: 50%;
  position: absolute;
  top: 25%;
  left: 50%;
  transform: translate(-50%, -50%);
}
.main .user__icons {
  grid-column: 6/7;
  grid-row: 2/3;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  height: 100%;
  list-style: none;
}
.main .user__icons__item {
  width: 100%;
  height: 100%;
}
.main .user__icons__item .link {
  display: flex;
  justify-content: flex-end;
  align-items: center;
  width: 100%;
  height: 100%;
}
.main .user__icons__item .link .link-svg {
  width: 3rem;
  height: 3rem;
}

.side-txt {
  grid-column: 2/3;
  grid-row: 1/3;
  writing-mode: vertical-rl;
  text-orientation: upright;
  font-size: 3rem;
  letter-spacing: 0.7rem;
  font-weight: 800;
}
.side-txt__span {
  -webkit-text-stroke-width: 2px;
  -webkit-text-stroke-color: black;
  -webkit-text-fill-color: white;
}

.text-back {
  position: absolute;
  top: 60%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 15rem;
  width: 100%;
  text-align: center;
  font-weight: 800;
  letter-spacing: 1rem;
  color: rgba(0, 0, 0, 0.05);
  text-shadow: 8px 6px 14px rgba(150, 150, 152, 0.28);
}

.hzz {
  grid-row: 2/3;
  grid-column: 3/6;
  text-align: center;
  font-size: 2rem;
  letter-spacing: 0.3rem;
}

